INSERT INTO `o1t1_document_articl0e0` VALUES (1,0,'<h1>\r\n\tPhPluSer1.1开发版发布&nbsp;\r\n</h1>\r\n<p>\r\n\t<br />\r\n</p>\r\n<p>\r\n\t<strong>PhPluSer是一个开源的内容管理框架，基于最新的ThinkPHP3.2版本开发，提供更方便、更安全的WEB应用开发体验，采用了全新的架构设计和命名空间机制，融合了模块化、驱动化和插件化的设计理念于一体，开启了国内WEB应用傻瓜式开发的新潮流。&nbsp;</strong> \r\n</p>\r\n<h2>\r\n\t主要特性：\r\n</h2>\r\n<p>\r\n\t1. 基于ThinkPHP最新3.2版本。\r\n</p>\r\n<p>\r\n\t2. 模块化：全新的架构和模块化的开发机制，便于灵活扩展和二次开发。&nbsp;\r\n</p>\r\n<p>\r\n\t3. 文档模型/分类体系：通过和文档模型绑定，以及不同的文档类型，不同分类可以实现差异化的功能，轻松实现诸如资讯、下载、讨论和图片等功能。\r\n</p>\r\n<p>\r\n\t4. 开源免费：PhPluSer遵循Apache2开源协议,免费提供使用。&nbsp;\r\n</p>\r\n<p>\r\n\t5. 用户行为：支持自定义用户行为，可以对单个用户或者群体用户的行为进行记录及分享，为您的运营决策提供有效参考数据。\r\n</p>\r\n<p>\r\n\t6. 云端部署：通过驱动的方式可以轻松支持平台的部署，让您的网站无缝迁移，内置已经支持SAE和BAE3.0。\r\n</p>\r\n<p>\r\n\t7. 云服务支持：即将启动支持云存储、云安全、云过滤和云统计等服务，更多贴心的服务让您的网站更安心。\r\n</p>\r\n<p>\r\n\t8. 安全稳健：提供稳健的安全策略，包括备份恢复、容错、防止恶意攻击登录，网页防篡改等多项安全管理功能，保证系统安全，可靠、稳定的运行。&nbsp;\r\n</p>\r\n<p>\r\n\t9. 应用仓库：官方应用仓库拥有大量来自第三方插件和应用模块、模板主题，有众多来自开源社区的贡献，让您的网站“One”美无缺。&nbsp;\r\n</p>\r\n<p>\r\n\t<br />\r\n</p>\r\n<p>\r\n\t<strong>&nbsp;PhPluSer集成了一个完善的后台管理体系和前台模板标签系统，让你轻松管理数据和进行前台网站的标签式开发。&nbsp;</strong> \r\n</p>\r\n<p>\r\n\t<br />\r\n</p>\r\n<h2>\r\n\t后台主要功能：\r\n</h2>\r\n<p>\r\n\t1. 用户Passport系统\r\n</p>\r\n<p>\r\n\t2. 配置管理系统&nbsp;\r\n</p>\r\n<p>\r\n\t3. 权限控制系统\r\n</p>\r\n<p>\r\n\t4. 后台建模系统&nbsp;\r\n</p>\r\n<p>\r\n\t5. 多级分类系统&nbsp;\r\n</p>\r\n<p>\r\n\t6. 用户行为系统&nbsp;\r\n</p>\r\n<p>\r\n\t7. 钩子和插件系统\r\n</p>\r\n<p>\r\n\t8. 系统日志系统&nbsp;\r\n</p>\r\n<p>\r\n\t9. 数据备份和还原\r\n</p>\r\n<p>\r\n\t<br />\r\n</p>\r\n<p>\r\n\t&nbsp;[ 官方下载：&nbsp;<a href=\"http://www.phplus.info/download.html\" target=\"_blank\">http://www.phplus.info/download.html</a>&nbsp;&nbsp;开发手册：<a href=\"http://document.phplus.info/\" target=\"_blank\">http://document.phplus.info/</a>&nbsp;]&nbsp;\r\n</p>\r\n<p>\r\n\t<br />\r\n</p>\r\n<p>\r\n\t<strong>PhPluSer开发团队 2013~2014</strong> \r\n</p>','',0);
